import React, { useEffect, useState } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';

const api_base = 'http://localhost:3001';

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");
  const [addingTodo, setAddingTodo] = useState(false);

  useEffect(() => {
    GetTodos();
  }, []);

  const GetTodos = () => {
    fetch(api_base + '/todos')
      .then((res) => res.json())
      .then((data) => setTodos(data))
      .catch((err) => console.error("Error: ", err));
  };

  const completeTodo = async (id) => {
    try {
      const response = await fetch(api_base + '/todo/complete/' + id);
      const data = await response.json();

      setTodos((todos) =>
        todos.map((todo) => {
          if (todo._id === data._id) {
            todo.complete = data.complete;
          }
          return todo;
        })
      );
    } catch (err) {
      console.error("Error completing todo: ", err);
    }
  };

  const addTodo = async () => {
    setAddingTodo(true);

    try {
      const response = await fetch(api_base + "/todo/new", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          text: newTodo,
        }),
      });

      const data = await response.json();

      setTodos([...todos, data]);
      setNewTodo("");
      setAddingTodo(false);
    } catch (err) {
      console.error("Error adding todo: ", err);
      setAddingTodo(false);
    }
  };

  const deleteTodo = async (id) => {
    try {
      const response = await fetch(api_base + '/todo/delete/' + id, {
        method: "DELETE",
      });

      const data = await response.json();

      setTodos((todos) =>
        todos.filter((todo) => todo._id !== data.result._id)
      );
    } catch (err) {
      console.error("Error deleting todo: ", err);
    }
  };

  return (
    <div className="App">
      <h1>Welcome, My Friend</h1>
      <h4>Your tasks</h4>

      <TransitionGroup className="todos">
        {todos.map((todo) => (
          <CSSTransition key={todo._id} timeout={500} classNames="todo">
            <div
              className={"todo" + (todo.complete ? " is-complete" : "")}
              onClick={() => completeTodo(todo._id)}
            >
              <div className="checkbox"></div>
              <div className="text">{todo.text}</div>
              <div className="delete-todo" onClick={() => deleteTodo(todo._id)}>
                x
              </div>
            </div>
          </CSSTransition>
        ))}
      </TransitionGroup>

      {addingTodo ? (
        <CSSTransition in={true} timeout={500} classNames="todo">
          <div className="todo zoom-scroll-animation">
            <div className="checkbox"></div>
            <div className="text">{newTodo}</div>
            <div className="delete-todo"></div>
          </div>
        </CSSTransition>
      ) : null}

      <div className="addButton" onClick={() => setAddingTodo(true)}>
        +
      </div>

      {addingTodo ? (
        <div className="popup">
          <input
            type="text"
            className="add-todo-input"
            onChange={(e) => setNewTodo(e.target.value)}
            value={newTodo}
          />
          <div className="button" onClick={addTodo}>
            Create Task
          </div>
        </div>
      ) : null}
    </div>
  );
}

export default App;

