// src/App.js

import React, { useState } from 'react';
import './App.css';
import TextInput from './components/TextInput';
import Response from './components/Response';
import { sendTextToBackend } from './services/api'; // Import the updated function
const textRoutes = require('./routes/textRoutes');
app.use('/api/text', textRoutes);


function App() {
  const [responseText, setResponseText] = useState('');

  const handleTextChange = async (text) => {
    try {
      const response = await sendTextToBackend(text);
      setResponseText(response.result);
    } catch (error) {
      console.error('Error sending text to backend:', error);
      setResponseText('Error occurred.');
    }
  };

  return (
    <div className="App">
      <h1>PWA Text Input</h1>
      <TextInput onTextChange={handleTextChange} buttonText="Send to Backend" />
      <Response responseText={responseText} />
    </div>
  );
}

export default App;
