// src/services/api.js

import axios from 'axios';

// src/services/api.js

const API_URL = 'http://localhost:5000/api/text'; // Update with your backend API URL

export const sendTextToBackend = async (text) => {
  try {
    const response = await axios.post(API_URL, { text });
    return response.data;
  } catch (error) {
    throw error;
  }
};
