// src/services/api.js

import axios from 'axios';

const API_URL = '/m';

export const sendTextToBackend = async (text) => {
  try {
    const response = await axios.post(`${API_URL}/your-api-endpoint`, {
      text,
    });
    return response.data;
  } catch (error) {
    throw error;
  }
};
